/*
Device List widget AngularJS controller code

Copyright (C) 2017-2023 Intel Corporation
SPDX-License-Identifier: Apache-2.0
*/

self.onInit = function() {
    const scope = self.ctx.$scope;
        scope.online = false;

    // Initialize the devices object
    scope.devices = [];
    self.ctx.datasources.forEach(source => {
        // Create the devices
        device = {
            selected: true,
            name: source.name,
            id: source.entityId
        };
        scope.devices.push(device);
    });
    scope.devices.sort((a, b) => {
        // Sort the devices by name alphabetically
        const an = a.name;
        const bn = b.name;
        return an < bn ? -1 : an > bn ? 1 : 0;
    });

    // Initialize the search object
    scope.search = {
        name: ""
    };

    // Batch alter the select state
    scope.batchSelect = state => {
        for (let name in scope.devices) {
            const device = scope.devices[name];
            device.selected = state;
        }
    };

    // Broadcast an event with the selected events
    scope.selected = () => {
        const selectedIds = [];
        scope.devices.forEach(device => {
            if (device.selected) {
                selectedIds.push({
                    id: device.id,
                    name: device.name
                });   
            }
        });
        const deviceSelected = new CustomEvent(
            'deviceSelected',
            {detail: selectedIds}
        );
        document.dispatchEvent(deviceSelected);
    };

    // Broadcast event on updates
    scope.$watch('devices', scope.selected, true);
};

self.onDataUpdated = function() {
    const scope = self.ctx.$scope;
    scope.selected();
};

self.onResize = function() {};

self.onDestroy = function() {};

