/*
Docker Container Stats widget AngularJS controller code

Copyright (C) 2017-2023 Intel Corporation
SPDX-License-Identifier: Apache-2.0
*/

self.onInit = function() {

    const scope = self.ctx.$scope;

    scope.containers = [];

    // Toggle search mode
    scope.filter = { name: "" };
    scope.searching = false;
    scope.search = () => {
        scope.searching = !scope.searching;
        if (scope.searching) {
            setTimeout(() => 
                document.getElementById("dcs-search-box").focus());
        } else {
            scope.filter.name = "";
        }
    };

    // Show waiting animation while command is being sent
    scope.waiting = false;
    document.addEventListener('rpcAck', rpcAck => {
        console.log(rpcAck.detail === false);
        if (rpcAck.detail === false) {
            scope.waiting = false;
        }
    });

    // Send an RPC request to refresh the stats
    scope.refresh = () => {
        const rpcRequest = new CustomEvent(
            'rpcRequest', {
                detail: {
                    command: "triggeraota",
                    payload: {
                        app: "docker",
                        cmd: "stats"
                    }
                }
            }
        );
        scope.waiting = true;
        document.dispatchEvent(rpcRequest);

        // Timeout the waiting animation
        setTimeout(
            () => scope.waiting = false,
            self.ctx.settings.refreshTimeout
        );
    };
};

self.onDataUpdated = function() {

    const scope = self.ctx.$scope;
    const isNumber = n => !isNaN(parseFloat(n)) && isFinite(n);

    let maxTS = 0;

    self.ctx.data.forEach(data => {
        // Timestamp is left, prop is right
        if (data.data.length === 0) return;
        let ts = data.data[0][0];
        let value = data.data[0][1];

        // Try to parse container stats
        let containers_data = null;
        try {
            containers_data = JSON.parse(value);
        } catch (e) {
            return;
        } finally {
            if (containers_data === null
                || !containers_data.hasOwnProperty("containers")) {
                    return;
                }
        }

        // Create container objects
        const containers = [];
        containers_data.containers.forEach(c => {
            container = {
                name: c.name,
                stats: {},
                sum: 0
            };
            for (const key in c) {
                if (key != "name") {
                    const val = c[key];
                    container.stats[key] = val;
                    if (! isNaN(val)) container.sum += val;
                }
            }
            containers.push(container);
        });

        containers.sort((a, b) => {
            // Non-zero containers come first
            // Logical XOR on a.sum and b.sum
            if (a.sum ? !b.sum : b.sum) {
                return a.sum > b.sum ? -1 : 1;
            }

            // Alphabetical sort
            an = a.name;
            bn = b.name;
            return an < bn ? -1 : an > bn ? 1 : 0;
        });

        // Update containers
        scope.containers = containers;

        if (ts > maxTS) {
            maxTS = ts;
        }
    });

    if (maxTS > 0) {
        const lastUpdatedTS = new Date(0);
        lastUpdatedTS.setUTCMilliseconds(maxTS);
        scope.lastUpdated = lastUpdatedTS.toLocaleString();
        setTimeout(() => scope.waiting = false, 10);
    }
};

self.onResize = function() {};

self.onDestroy = function() {};

// This is a customized version of the built in Time Series Table
// It will broadcast a deviceActive event when new data comes in

self.onInit = function() {
    const scope = self.ctx.$scope;
    const id = self.ctx.$scope.$injector.get('utils').guid();
    scope.tableId = "table-" + id;
    scope.ctx = self.ctx;

    scope.deviceActive = new Event("deviceActive");
    scope.dataSigs = [];
};

self.onDataUpdated = function() {
    const scope = self.ctx.$scope;

    // Update the table
    scope.$broadcast(
        'timeseries-table-data-updated', scope
        .tableId);

    // Function to recapture the data signature
    const captureSigs = sources => {
        scope.dataSigs = [];
        const dataSigs = [];
        for (const s of sources) {
            if (s.data.length === 0) return;
            dataSig = {
                length: s.data.length,
                head: s.data[0],
                tail: s.data[s.data.length - 1]
            };
            dataSigs.push(dataSig);
        }
        scope.dataSigs = dataSigs;
    };

    // Check the current data against the data signature
    sources = self.ctx.defaultSubscription.data;
    if (scope.dataSigs.length === 0) {
        captureSigs(sources);
    } else {
        changed = false;
        for (let i = 0; i < scope.dataSigs.length; ++
            i) {
            sig = scope.dataSigs[i];
            data = sources[i].data;
            if (data.length != sig.length ||
                data[0][0] != sig.head[0] ||
                data[data.length - 1][0] != sig.tail[0]
            ) {
                changed = true;
                break;
            }
        }
        if (changed) {
            scope.dataSigs = [];
            captureSigs(sources);
            document.dispatchEvent(scope.deviceActive);
        }
    }
};

self.actionSources = function() {
    return {
        'actionCellButton': {
            name: 'widget-action.action-cell-button',
            multiple: true
        },
        'rowClick': {
            name: 'widget-action.row-click',
