/*
OTA Form widget AngularJS controller code

@copyright: Copyright 2019-20232 Intel Corporation All Rights Reserved.
@license: Intel, see licenses/LICENSE for more details.
*/

self.onInit = function() {

    const scope = self.ctx.$scope;
    const settings = self.ctx.settings;

    // Callback to show the prerendered dialog
    $mdDialog = scope.$injector.get("$mdDialog");
    scope.displayDialog = event => {
        $mdDialog.show({
            contentElement: '#ota-dialog-' + scope.otaMethod,
            parent: angular.element(document.body),
            targetEvent: event,
            clickOutsideToClose: true
        });
    };

    // Callback to hide the dialog
    scope.closeDialog = () => {
        $mdDialog.cancel();
        scope.initOtaResponse();
    };

    // Set the method
    scope.otaMethodLabel = settings.otaMethodLabel;
    scope.otaMethod = settings.otaMethod;

    // Set the method select fields
    scope.otaSelectFields = [];
    if (settings.otaSelectFields) {
        settings.otaSelectFields.forEach(field => {
            scope.otaSelectFields.push(field);
        });
    }

    // Set the method text fields
    scope.otaTextFields = [];
    if (settings.otaTextFields) {
        settings.otaTextFields.forEach(field => {
            scope.otaTextFields.push(field);
        });
    }

    // Center button if no fields are present
    scope.alignButton =
        (scope.otaSelectFields.length || scope.otaTextFields.length)
        ? "flex-end" : "center";

    // The device response data
    scope.initOtaResponse = () => {
        scope.otaTab = 1;
        scope.otaSuccess = 0;
        scope.otaFailure = 0;
        scope.otaResponse = [];
    };
    scope.initOtaResponse();

    scope.addOtaResponse = (success, name, message) => {
        response = {
            'success': success,
            'name': name,
            'message': message
        };
        (success) ? ++scope.otaSuccess : ++scope.otaFailure;
        scope.otaResponse.push(response);
    };

    // Callback for send button
    scope.batch = settings.batch;
    scope.send = () => {
        scope.initOtaResponse();
        if (scope.batch) {
            scope.sendFieldsBatch();
        } else {
            scope.sendFields();
        }
    };

    // Simulate a batch RPC
    scope.devices = [];
    scope.sendFieldsBatch = () => {
        scope.devices.forEach(device => {
            self.ctx.defaultSubscription.targetDeviceId = device.id;
            self.ctx.defaultSubscription.targetDeviceName = device.name;
            scope.sendFields();
        });
    };

    // Send the fields and inputs to device
    scope.sendFields = () => {

        // Populate the method arguments
        const args = {};
        scope.otaTextFields.forEach(field => {
            if (field.data) args[field.key] = field.data;
        });
        scope.otaSelectFields.forEach(field => {
            if (field.data) args[field.key] = field.data.key;
        });

        // Send the arguments
        const timeout = settings.requestTimeout;
        const name = self.ctx.defaultSubscription.targetDeviceName;
        self.ctx.controlApi
            .sendTwoWayCommand(
                scope.otaMethod, args, timeout)
            .then(
                // Handle success
                success => scope.addOtaResponse(
                    true, name, angular.toJson(success)),
                // Handle failure
                failure => scope.addOtaResponse(
                    false, name, `"${failure.status}`
                    + ` ${failure.statusText} `
                    + `${scope.rpcErrorText}"`)
            )
            .finally(() => {
                const totalOta = scope.otaSuccess + scope.otaFailure;
                if (totalOta >= scope.devices.length) { // Include non-batch case
                    scope.otaResponse.sort((a, b) => {
                        if (a.success && !b.success) {
                            return -1;
                        } else if (!a.success && b.success) {
                            return 1;
                        } else if (a.name < b.name) {
                            return -1;
                        } else if (a.name > b.name) {
                            return 1;
                        }
                        return 0;
                    });
                    scope.otaTab = 2;
                }
            });
    };

    // Save data as a .csv file
    // Adapted from https://stackoverflow.com/a/30832210
    scope.save = () => {
        // Create .CSV filename
        const cmd = scope.otaMethodLabel
            .toLocaleLowerCase()
            .replace(" ", "-");
        const date = new Date();
        const d = date.getDate();
        const m = date.getMonth() + 1;
        const y = date.getFullYear();
        filename = `${cmd}_${y}-${m}-${d}.csv`;
        
        // Create .CSV file
        data = "Send Status,Device Name,Message\r\n";
        scope.otaResponse.forEach(r => {
            data += `${r.success},${r.name},${r.message}\r\n`;
        });
        
        type = "text/plain";
        const file = new Blob([data], {type: type});
        if (window.navigator.msSaveOrOpenBlob)
            window.navigator.msSaveOrOpenBlob(file, filename); // IE10+
        else {
            const a = document.createElement("a"),
                    url = URL.createObjectURL(file);
            a.href = url;
            a.download = filename;
            document.body.appendChild(a);
            a.click();
            setTimeout(function() {
                document.body.removeChild(a);
                window.URL.revokeObjectURL(url);
            }, 0);
        }
    };

    // Listen to the globally broadcasted device selection event
    scope.updateList = e => scope.devices = e.detail;
    document.addEventListener("deviceSelected", scope.updateList);

    // Run any resizing code
    self.onResize();
};

self.onResize = function() {};

self.onDestroy = function() {
    document.removeEventListener("deviceActive", self.ctx.$scope.updateList);
};

