/*
Connectivity Status widget AngularJS controller code

@copyright: Copyright 2019-2023 Intel Corporation All Rights Reserved.
@license: Intel, see licenses/LICENSE for more details.
*/

self.onInit = function() {

    const scope = self.ctx.$scope;
    const settings = self.ctx.settings;

    // Initialization
    scope.connection = "";
    scope.pulse = 60000 * settings.pulse;

    // Callback to send the fields and inputs to device
    scope.ping = () => {

        // Send the arguments
        promise = self.ctx.controlApi
            .sendTwoWayCommand("ping", {}, 500);
        promise.then(
            success => scope.connection =
            "alive",
            failure => scope.connection = ""
        );
        return promise;

    };

    // Defer the check in ping to avoid spam
    scope.deferred_ping = 0;
    scope.defer_ping = () => {
        clearTimeout(scope.deferred_ping);
        scope.deferred_ping = setTimeout(scope.ping, 5000);
    };

    // Recursive pulse ping
    scope.pulse_ping = () => setTimeout(() => {
        scope.defer_ping();
        scope.pulse_ping();
    }, scope.pulse);

    // Listen to the globally broadcasted connectivity event
    document.addEventListener("deviceActive", scope
        .defer_ping);

    // Send an RPC on behalf of a widget that can't
    scope.sendRpc = rpcEvent => {
        if (rpcEvent.detail !== null) {
            command = rpcEvent.detail.command;
            payload = rpcEvent.detail.payload;
            self.ctx.controlApi
                .sendTwoWayCommand(command, payload, 500)
                .then(
                    success => document.dispatchEvent(
                        new CustomEvent(
                            'rpcAck', { detail: true }
                        )
                    ),
                    failure => document.dispatchEvent(
                        new CustomEvent(
                            'rpcAck', { detail: false }
                        )
                    )
                )
        }
    };

    // Listen for globally broadcasted RPC request events
    document.addEventListener("rpcRequest", scope.sendRpc);

    scope.ping();
    scope.pulse_ping();
};

self.onDestroy = function() {
    document.removeEventListener("deviceActive",
        self.ctx.$scope.ping);
};
