// This is a customized version of the built in Time Series Flot
// It will broadcast a deviceActive event when new data comes in

self.onInit = function() {
    const scope = self.ctx.$scope;
    self.ctx.flot = new TbFlot(self.ctx);
    scope.deviceActive = new Event("deviceActive");
    scope.dataSigs = [];
};

self.onDataUpdated = function() {
    const scope = self.ctx.$scope;

    // Update the charts
    self.ctx.flot.update();

    // Function to recapture the data signature
    const captureSigs = sources => {
        scope.dataSigs = [];
        const dataSigs = [];
        for (const s of sources) {
            if (s.data.length === 0) return;
            dataSig = {
                length: s.data.length,
                head: s.data[0],
                tail: s.data[s.data.length - 1]
            };
            dataSigs.push(dataSig);
        }
        scope.dataSigs = dataSigs;
    };

    // Check the current data against the data signature
    sources = self.ctx.defaultSubscription.data;
    if (scope.dataSigs.length === 0) {
        captureSigs(sources);
    } else {
        changed = false;
        for (let i = 0; i < scope.dataSigs.length; ++
            i) {
            sig = scope.dataSigs[i];
            data = sources[i].data;
            if (data.length != sig.length ||
                data[0][0] != sig.head[0] ||
                data[data.length - 1][0] != sig.tail[0]
            ) {
                changed = true;
                break;
            }
        }
        if (changed) {
            scope.dataSigs = [];
            captureSigs(sources);
            document.dispatchEvent(scope.deviceActive);
        }
    }
};

self.onResize = function() {
    self.ctx.flot.resize();
};

self.onEditModeChanged = function() {
    self.ctx.flot.checkMouseEvents();
};

self.onMobileModeChanged = function() {
    self.ctx.flot.checkMouseEvents();
};

self.getSettingsSchema = function() {
    return TbFlot.settingsSchema('graph');
};

self.getDataKeySettingsSchema = function() {
    return TbFlot.datakeySettingsSchema(true);
};

self.onDestroy = function() {
    self.ctx.flot.destroy();
};

