        function DialogController(instance) {
            let vm = instance;
            
            vm.closeDialog = () => {
                vm.dialogRef.close(null);
                vm.initOtaResponse();
            };

            // Set the method
            vm.otaMethodLabel = settings.otaMethodLabel;
            vm.otaMethod = settings.otaMethod;

            vm.batch = settings.batch ? true : false;

            // Set the method select fields
            vm.otaSelectFields = [];
            if (settings.otaSelectFields) {
                settings.otaSelectFields.forEach(field => {
                    vm.otaSelectFields.push(field);
                });
            }
            
             // Set the method text fields
            vm.otaTextFields = [];
            if (settings.otaTextFields) {
                settings.otaTextFields.forEach(field => {
                vm.otaTextFields.push(field);
                });
            }
            
            // Center button if no fields are present
            vm.alignButton =
                (vm.otaSelectFields.length || vm.otaTextFields.length)
                ? "flex-end" : "center";

            // The device response data
            vm.initOtaResponse = () => {
                vm.otaTab = 0;
                vm.otaSuccess = 0;
                vm.otaFailure = 0;
                vm.otaResponse = [];
            };
            vm.initOtaResponse();
            
            vm.addOtaResponse = (success, name, message) => {
                response = {
                    'success': success,
                    'name': name,
                    'message': message
                };
                (success) ? ++vm.otaSuccess : ++vm.otaFailure;
                vm.otaResponse.push(response);
            };
            
            // Callback for send button
            vm.send = () => {
                vm.initOtaResponse();
                if (vm.batch) {
                    vm.sendFieldsBatch();
                } else {
                    vm.sendFields();
                }
            };
            
             // Simulate a batch RPC
            vm.sendFieldsBatch = () => {
                self.ctx.detectChanges();
                console.log(scope.devices);
                scope.devices.forEach(device => {
                    console.log('-processing a device-');
                    self.ctx.detectChanges();
                    self.ctx.defaultSubscription.targetDeviceId = device.id;
                    self.ctx.defaultSubscription.targetDeviceName = device.name;
                    vm.sendFields();
                });
            };
            
            // Send the fields and inputs to device
            vm.sendFields = () => {
                // Populate the method arguments
                const args = {};
        
                vm.otaTextFields.forEach(field => {
                    if (field.data) args[field.key] = field.data;
                });
        
                vm.otaSelectFields.forEach(field => {
                    if (field.data) args[field.key] = field.data.key;
                });
                // Send the arguments
                const timeout = settings.requestTimeout;
                const name = self.ctx.defaultSubscription.targetDeviceName;
        
                var commandObservable = self.ctx.controlApi.sendTwoWayCommand(scope.otaMethod, args, timeout);
                vm.afterOtaResponse = () => {
                
                    const totalOta = vm.otaSuccess + vm.otaFailure;
                    
                    if (totalOta >= scope.devices.length) { // Include non-batch case
                        vm.otaResponse.sort((a, b) => {
                        if (a.success && !b.success) {
                            return -1;
                        } else if (!a.success && b.success) {
                            return 1;
                        } else if (a.name < b.name) {
                            return -1;
                        } else if (a.name > b.name) {
                            return 1;
                        }
                            return 0;
                        });
                        vm.otaTab = 1;
                    }
                }
                commandObservable.subscribe(
                    function (response) {
                        vm.addOtaResponse(
                            true, name, response);
                        vm.afterOtaResponse();
                        self.ctx.detectChanges();
                    },
                    function (rejection) {
                        self.ctx.detectChanges();
                        if (rejection.status == 504) {
                            vm.addOtaResponse(
                                false, name, "Device did not respond before timeout.");
                        } else {
                            vm.addOtaResponse(
                                false, name, `"${rejection.status}`
                                + ` ${rejection.statusText} `
                                + `${vm.rpcErrorText}"`);
                        }
                        vm.afterOtaResponse();
                        self.ctx.detectChanges();
                    }
                );
            };
        }
    };
    
    //Listen to the globally broadcasted device selection event
    document.addEventListener("deviceSelected", e => {
        console.log("Updating devices with " + e.detail);
        scope.devices = e.detail;
    }); 
    
    // Run any resizing code
    self.onResize();
};

self.onResize = function() {};

self.onDestroy = function() {
    document.removeEventListener("deviceActive", self.ctx.$scope.updateList);
};

