@copyright: Copyright 2021 Intel Corporation All Rights Reserved.
@license: Intel, see licenses/LICENSE for more details.
*/

self.onInit = function() {

    const scope = self.ctx.$scope;

    scope.containers = [];

    // Toggle search mode
    scope.filter = { name: "" };
    scope.searching = false;
    scope.search = () => {
        scope.searching = !scope.searching;
        if (scope.searching) {
            setTimeout(() => 
                document.getElementById("dcs-search-box").focus());
        } else {
            scope.filter.name = "";
        }
    };

    // Show waiting animation while command is being sent
    scope.waiting = false;
    document.addEventListener('rpcAck', rpcAck => {
        console.log(rpcAck.detail === false);
        if (rpcAck.detail === false) {
            scope.waiting = false;
        }
    });

    // Send an RPC request to refresh the stats
    scope.refresh = () => {
        const rpcRequest = new CustomEvent(
            'rpcRequest', {
                detail: {
                    command: "triggeraota",
                    payload: {
                        app: "docker",
                        cmd: "stats"
                    }
                }
            }
        );
        scope.waiting = true;
        document.dispatchEvent(rpcRequest);

        // Timeout the waiting animation
        setTimeout(
            () => scope.waiting = false,
            self.ctx.settings.refreshTimeout
        );
    };
};

self.onDataUpdated = function() {

    const scope = self.ctx.$scope;
    const isNumber = n => !isNaN(parseFloat(n)) && isFinite(n);

    let maxTS = 0;

    self.ctx.data.forEach(data => {
        // Timestamp is left, prop is right
        if (data.data.length === 0) return;
        let ts = data.data[0][0];
        let value = data.data[0][1];

        // Try to parse container stats
        let containers_data = null;
        try {
            containers_data = JSON.parse(value);
        } catch (e) {
            return;
        } finally {
            if (containers_data === null
                || !containers_data.hasOwnProperty("containers")) {
                    return;
                }
        }

        // Create container objects
        const containers = [];
        containers_data.containers.forEach(c => {
            container = {
                name: c.name,
                stats: {},
                sum: 0
            };
            for (const key in c) {
                if (key != "name") {
                    const val = c[key];
                    container.stats[key] = val;
                    if (! isNaN(val)) container.sum += val;
                }
            }
            containers.push(container);
        });

        containers.sort((a, b) => {
            // Non-zero containers come first
            // Logical XOR on a.sum and b.sum
            if (a.sum ? !b.sum : b.sum) {
                return a.sum > b.sum ? -1 : 1;
            }

            // Alphabetical sort
            an = a.name;
            bn = b.name;
            return an < bn ? -1 : an > bn ? 1 : 0;
        });

        // Update containers
        scope.containers = containers;

        if (ts > maxTS) {
            maxTS = ts;
        }
    });

    if (maxTS > 0) {
        const lastUpdatedTS = new Date(0);
        lastUpdatedTS.setUTCMilliseconds(maxTS);
        scope.lastUpdated = lastUpdatedTS.toLocaleString();
        setTimeout(() => scope.waiting = false, 10);
    }
};

self.onResize = function() {};

self.onDestroy = function() {};
